export function bem(widgetName: string) {
  const cx = function(element?: string, subElement?: string) {
    if (element) {
      const scoppedWidgetName = `ais-${widgetName}-${element}`;

      // output `ais-Widget-Header|Body|Footer ais-Header|Body|Footer`
      if (element === 'header' || element === 'body' || element === 'footer') {
        const nonScoppedWidgetName = `ais-${element}`;
        return `${scoppedWidgetName} ${nonScoppedWidgetName}`;
      }

      // output `ais-Widget-Xyz--abc`
      if (subElement) {
        return `${scoppedWidgetName}--${subElement}`;
      }

      // output `ais-Widget-Xyz`
      return scoppedWidgetName;
    }

    // output `ais-Widget`
    return `ais-${widgetName}`;
  };
  return cx;
}

export function parseNumberInput(input?: number | string) {
  return typeof input === 'string' ? parseInt(input, 10) : input;
}

export function noop(...args: any[]): void {}

export function capitalize(s) {
  return s.charAt(0).toUpperCase() + s.slice(1);
}
import { Input, OnDestroy, OnInit } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { bem, noop } from './utils';

export class Widget {
  public init: (
    params: {
      templatesConfig: object;
      state: object;
      results: object[];
      createURL: (value: any) => string;
      instantSearchInstance: object;
    }
  ) => void;
  public getConfiguration?: () => object;
  public render: (
    params: {
      templatesConfig: object;
      state: object;
      results: {}[];
      createURL: (value: any) => string;
      instantSearchInstance: object;
    }
  ) => void;
  public dispose: (
    params: {
      helper: object;
      state: object;
    }
  ) => object | void;
}

export type Connector = (
  renderFn: (state: object, isFirstRendering: boolean) => void,
  unmountFn: () => void
) => (widgetOptions?: object) => Widget;

export class BaseWidget implements OnInit, OnDestroy {
  public instantSearchParent: any;

  @Input() public autoHideContainer?: boolean;

  public widget?: Widget;
  public state?: object = {};
  public cx: Function;

  constructor(widgetName: string) {
    this.cx = bem(widgetName);
  }

  public createWidget(connector: Connector, options: object = {}) {
    this.widget = connector(this.updateState, noop)(options);
  }

  public ngOnInit() {
    // add widget to the InstantSearch Instance
    this.instantSearchParent.addWidget(this.widget);
  }

  public ngOnDestroy() {
    if (isPlatformBrowser(this.instantSearchParent.platformId)) {
      this.instantSearchParent.removeWidget(this.widget);
    }
  }

  public updateState = (
    state: {},
    isFirstRendering: boolean
  ): Promise<void> | void => {
    if (isFirstRendering) {
      return Promise.resolve().then(() => {
        this.state = state;
      });
    }

    this.state = state;
  };

  // helper method for genering item list className
  public getItemClass(item: { isRefined?: boolean }) {
    let className = this.cx('item');

    if (item.isRefined) {
      className = `${className} ${this.cx('item', 'selected')}`;
    }

    return className;
  }
}
export const VERSION = '2.3.0';
import {
  AfterViewInit,
  Component,
  Input,
  OnInit,
  OnDestroy,
  Output,
  EventEmitter,
  Inject,
  PLATFORM_ID,
  VERSION as AngularVersion,
} from '@angular/core';
import { isPlatformBrowser } from '@angular/common';

import * as algoliasearchProxy from 'algoliasearch/lite';
import instantsearch from 'instantsearch.js/es';
import { AlgoliaSearchHelper } from 'algoliasearch-helper';

import { Widget } from '../base-widget';
import { VERSION } from '../version';

const algoliasearch = algoliasearchProxy.default || algoliasearchProxy;

export type SearchRequest = {
  indexName: string;
  params: SearchRequestParameters;
};

export type SearchForFacetValuesRequest = {
  indexName: string;
  params: SearchForFacetValuesRequestParameters;
};

// Documentation: https://www.algolia.com/doc/api-reference/search-api-parameters/
export type SearchParameters = {
  // Attributes
  attributesToRetrieve?: string[];
  restrictSearchableAttributes?: string[];

  // Filtering
  filters?: string;
  facetFilters?: string[];
  optionalFilters?: string[];
  numericFilters?: string[];
  sumOrFiltersScores?: boolean;

  // Faceting
  facets?: string[];
  maxValuesPerFacet?: number;
  facetingAfterDistinct?: boolean;
  sortFacetValuesBy?: string;

  // Highlighting / Snippeting
  attributesToHighlight?: string[];
  attributesToSnippet?: string[];
  highlightPreTag?: string;
  highlightPostTag?: string;
  snippetEllipsisText?: string;
  restrictHighlightAndSnippetArrays?: boolean;

  // Pagination
  page?: number;
  hitsPerPage?: number;
  offset?: number;
  length?: number;

  // Typos
  minWordSizefor1Typo?: number;
  minWordSizefor2Typos?: number;
  typoTolerance?: string | boolean;
  allowTyposOnNumericTokens?: boolean;
  ignorePlurals?: boolean | string[];
  disableTypoToleranceOnAttributes?: string[];

  // Geo-Search
  aroundLatLng?: string;
  aroundLatLngViaIP?: boolean;
  aroundRadius?: number | 'all';
  aroundPrecision?: number;
  minimumAroundRadius?: number;
  insideBoundingBox?: GeoRectangle | GeoRectangle[];
  insidePolygon?: GeoPolygon | GeoPolygon[];

  // Query Strategy
  queryType?: string;
  removeWordsIfNoResults?: string;
  advancedSyntax?: boolean;
  optionalWords?: string | string[];
  removeStopWords?: boolean | string[];
  disableExactOnAttributes?: string[];
  exactOnSingleWordQuery?: string;
  alternativesAsExact?: string[];

  // Query Rules
  enableRules?: boolean;
  ruleContexts?: string[];

  // Advanced
  minProximity?: number;
  responseFields?: string[];
  maxFacetHits?: number;
  percentileComputation?: boolean;
  distinct?: number | boolean;
  getRankingInfo?: boolean;
  clickAnalytics?: boolean;
  analytics?: boolean;
  analyticsTags?: string[];
  synonyms?: boolean;
  replaceSynonymsInHighlight?: boolean;
};

export interface SearchRequestParameters extends SearchParameters {
  query: string;
}

export interface SearchForFacetValuesRequestParameters
  extends SearchParameters {
  facetQuery: string;
  facetName: string;
}

export type GeoRectangle = [number, number, number, number];
export type GeoPolygon = [number, number, number, number, number, number];

// Documentation: https://www.algolia.com/doc/rest-api/search/?language=javascript#search-multiple-indexes
export type SearchResponse = {
  hits: Hit[];
  page?: number;
  nbHits?: number;
  nbPages?: number;
  hitsPerPage?: number;
  processingTimeMS?: number;
  query?: string;
  params?: string;
  index?: string;
};

export type Hit = {
  _highlightResult?: object;
};

// Documentation: https://www.algolia.com/doc/rest-api/search/?language=javascript#search-for-facet-values
export type SearchForFacetValuesResponse = {
  value: string;
  highlighted?: string;
  count?: number;
};

export type SearchClient = {
  addAlgoliaAgent?: (agent: string) => void;
  search: (requests: SearchRequest[]) => Promise<{ results: SearchResponse[] }>;
  searchForFacetValues?: (
    requests: SearchForFacetValuesRequest[]
  ) => Promise<{ facetHits: SearchForFacetValuesResponse[] }[]>;
};

export type InstantSearchConfig = {
  appId?: string;
  apiKey?: string;
  indexName: string;

  numberLocale?: string;
  searchFunction?: (helper: AlgoliaSearchHelper) => void;
  createAlgoliaClient?: (
    algoliasearch: Function,
    appId: string,
    apiKey: string
  ) => object;
  searchClient?: SearchClient;
  searchParameters?: SearchParameters | void;
  urlSync?:
    | boolean
    | {
        mapping?: object;
        threshold?: number;
        trackedParameters?: string[];
        useHash?: boolean;
        getHistoryState?: () => object;
      };
  routing?:
    | boolean
    | {
        router?: {
          onUpdate: (cb: (object) => void) => void;
          read: () => object;
          write: (routeState: object) => void;
          createURL: (routeState: object) => string;
          dispose: () => void;
        };
        stateMapping?: {
          stateToRoute(object): object;
          routeToState(object): object;
        };
      };
};

export class InstantSearchInstance {
  public start: () => void;

  public addWidget: (widget: Widget) => void;
  public addWidgets: (widgets: Widget[]) => void;

  public removeWidget: (widget: Widget) => void;
  public removeWidgets: (widgets: Widget[]) => void;

  // EventEmmiter
  public on: (eventName: string, callback: Function) => void;
  public removeListener: (eventName: string, callback: Function) => void;

  public helper: {
    lastResults: Object;
    state: Object;
  };

  public refresh: () => void;
  public dispose: () => void;
}

@Component({
  selector: 'ais-instantsearch',
  template: `<ng-content></ng-content>`,
})
export class NgAisInstantSearch implements AfterViewInit, OnInit, OnDestroy {
  @Input() public config: InstantSearchConfig;
  @Input() public instanceName: string = 'default';

  @Output()
  change: EventEmitter<{ results: {}; state: {} }> = new EventEmitter<{
    results: {};
    state: {};
  }>();

  public instantSearchInstance: InstantSearchInstance;

  constructor(@Inject(PLATFORM_ID) private platformId: Object) {}

  public ngOnInit() {
    this.createInstantSearchInstance(this.config);
  }

  public ngAfterViewInit() {
    this.instantSearchInstance.start();
  }

  public ngOnDestroy() {
    this.instantSearchInstance.removeListener('render', this.onRender);
    this.instantSearchInstance.dispose();
  }

  public createInstantSearchInstance(config: InstantSearchConfig) {
    // add default searchParameters with highlighting config
    if (!config.searchParameters) config.searchParameters = {};
    Object.assign(config.searchParameters, {
      highlightPreTag: '__ais-highlight__',
      highlightPostTag: '__/ais-highlight__',
    });

    // remove URLSync widget if on SSR
    if (!isPlatformBrowser(this.platformId)) {
      if (typeof config.urlSync !== 'undefined') delete config.urlSync;
      if (typeof config.routing !== 'undefined') delete config.routing;
    }

    if (!config.searchClient && !config.createAlgoliaClient) {
      const client = algoliasearch(config.appId, config.apiKey);
      config.searchClient = client;
      config.appId = undefined;
      config.apiKey = undefined;
    }

    // custom algolia client agent
    if (typeof config.searchClient.addAlgoliaAgent === 'function') {
      // add user agents
      config.searchClient.addAlgoliaAgent(`angular (${AngularVersion.full})`);
      config.searchClient.addAlgoliaAgent(`angular-instantsearch (${VERSION})`);
    }
    this.instantSearchInstance = instantsearch(config);
    this.instantSearchInstance.on('render', this.onRender);
  }

  public addWidget(widget: Widget) {
    this.instantSearchInstance.addWidget(widget);
  }

  public removeWidget(widget: Widget) {
    this.instantSearchInstance.removeWidget(widget);
  }

  public refresh() {
    this.instantSearchInstance.refresh();
  }

  onRender = () => {
    this.change.emit({
      results: this.instantSearchInstance.helper.lastResults,
      state: this.instantSearchInstance.helper.state,
    });
  };
}
import { Component, Input, Inject, forwardRef } from '@angular/core';
import { connectBreadcrumb } from 'instantsearch.js/es/connectors';
import { BaseWidget } from '../base-widget';
import { NgAisInstantSearch } from '../instantsearch/instantsearch';
import { noop } from '../utils';

export type BreadcrumbState = {
  createURL: Function;
  items: BreadcrumbItem[];
  refine: Function;
};

export type BreadcrumbItem = {
  name: string;
